
CREATE TABLE `contents_files` (
	`id` INT(11) NOT NULL AUTO_INCREMENT,
	`name` VARCHAR(255) NULL DEFAULT NULL COLLATE 'utf8mb4_general_ci',
	`url` VARCHAR(255) NULL DEFAULT NULL COLLATE 'utf8mb4_general_ci',
	`bucket` VARCHAR(255) NULL DEFAULT NULL COLLATE 'utf8mb4_general_ci',
	`type` VARCHAR(255) NULL DEFAULT NULL COLLATE 'utf8mb4_general_ci',
	`format` VARCHAR(255) NULL DEFAULT NULL COLLATE 'utf8mb4_general_ci',
	`size` VARCHAR(255) NULL DEFAULT NULL COLLATE 'utf8mb4_general_ci',
	`peso` VARCHAR(255) NULL DEFAULT NULL COLLATE 'utf8mb4_general_ci',
	`embed` VARCHAR(255) NULL DEFAULT NULL COLLATE 'utf8mb4_general_ci',
	`content_id` BIGINT(20) UNSIGNED DEFAULT '0',
	`created_at` DATETIME NULL DEFAULT current_timestamp(),
	`updated_at` DATETIME NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
	`deleted_at` DATETIME NULL DEFAULT NULL,
	PRIMARY KEY (`id`) USING BTREE
);


